solution_code:
#include <iostream>
#include <vector>
using namespace std;

int main() {
    int n;
    cout << "Entrez le nombre de valeurs: ";
    cin >> n;

    vector<double> nombres(n);
    double somme = 0;

    for(int i = 0; i < n; i++) {
        cout << "Entrez le nombre " << i+1 << ": ";
        cin >> nombres[i];
        somme += nombres[i];
    }

    double moyenne = somme / n;
    cout << "La moyenne est: " << moyenne << endl;

    return 0;
}
